INSERT INTO Questions (Test_Id, Question, Answer_1, Answer_2, Answer_3, Answer_4, Right_Answer, Paragraph, Image)
  VALUES
  (1, 'Как следует поступить водителю желтого легкового автомобиля в показанной ситуации?', 'Остановиться только на левой обочине (А)', 'Остановиться только на правой обочине (Б)', 'Остановиться на правой (Б) или на левой (А) обочине', NULL, -- Answer_4
  '3', --Right_Answer
  'Изучите п.26 ПДД', -- Paragraph
  '/VIEW/RESOURCES/IMG/1-1-1.jpg' -- Image
  ),
  (1, 'Согласно Правилам, водителю запрещается учавствовать в движении на транспортном средстве с загрязнениями, не позволяющими видеть информацию на регистрационном знаке с расстояния:', '100 метров и менее.', '50 метров и менее.', '40 метров и менее.', '10 метров и менее.', '3', 'Изучите п.10.3 ПДД', '/VIEW/RESOURCES/IMG/1-1-2.jpg'),
  (1, 'Какое из перечисленных дорожных покрытий является усовершенствованным?', 'Покрытие из асфальтобетонных смесей.', 'Покрытие из булыжника.', 'Покрытие из брусчатки.', 'Все перечисленные дорожные покрытия являются усовершенствованными.', '4', -- Right_Answer
  'Изучите п.2.75 ПДД', -- Paragraph
  '/VIEW/RESOURCES/IMG/1-1-3.jpg' -- Image
  ),
  (1, 'В каких случаях водителю не запрещено управлять транспортным средством?', 'Если он находится под воздействием лекарственных препаратов, повышающих быстроту реакции и внимания.', 'Если он пользуется во время движения телефонной связью, при отсутствии устройства, позволяющего вести переговоры без использования рук.', 'Если он находится в утомленном состоянии, ставящем под угрозу безопасность дорожного движения.', 'Во всех случаях запрещено', '1', 'Изучите п.10 ПДД', NULL),
  (1, 'Является ли островком безопасности участок пешеходного перехода, проходящий через трамвайные пути?', 'Является.', 'Не является.', NULL, NULL, '2', 'Изучите п.2.42 ПДД', '/VIEW/RESOURCES/IMG/1-1-5.jpg'),
  (1, 'Кто из перечисленных лиц при управлении механическим транспортным средством, согласно ПДД, является водителем?', 'Лицо, сдающее квалификационный практический экзамен на право управления механическим транспортным средством.', 'Лицо, обучаемое управлению механическим транспортным средством.', 'Лицо, управляющее транспортным средством, не имеющее водительского удостоверения.', 'Все перечисленные являются водителями', '3', 'Изучите п.2.8 ПДД', NULL),
  (1, 'Разрешено ли пассажиру при поездке на мотоцикле быть без застегнутого мотошлема?', 'Разрешено при поездке ребенка до семи лет в боковом прицепе.', 'Запрещено.', 'Разрешено при поездке в боковом прицепе.', NULL, '2', 'Изучите п.9.5 ПДД', NULL),
  (1, 'Делает ли дорогу без усовершенствованного покрытия наличие участка с усовершенствованным покрытием непосредственно перед перекрестком равной по значению с пересекаемой дорогой с усовершенствованным покрытием?', 'Не делает.', 'Делает', NULL, NULL, '1', 'Изучите п.2.11 ПДД', '/VIEW/RESOURCES/IMG/1-1-8.jpg'),
  (1, 'В показанной ситуации Вы при приближении встречного транспортного средства оперативного назначения с включенными маячками синего цвета:', 'Обязаны остановиться справа у тротуара.', 'Можете продолжать движение, уступая дорогу и обеспечивая беспрепятственный проезд транспортному средству оперативного назначения.', NULL, NULL, '2', 'Изучите п.26 ПДД', '/VIEW/RESOURCES/IMG/1-1-9.jpg'),
  (1, 'Имеет ли право водитель на возмещение причиненного ему или его имуществу вреда в порядке, установленном законодательством?', 'Имеет в любом случае.', 'Имеет, только если его автомобиль застрахован.', NULL, NULL, '1', 'Изучите п.6.5 ПДД', NULL),
  --2
  (2, 'Какие знаки из показанных устанавливают непосредственно перед железнодорожным переездом?', 'Только знаки 1 и 3.', 'Только знак 1.', 'Только знак 2.', 'Только знак 3', '4', 'Изучите приложение 2 пар.1 ПДД', '/VIEW/RESOURCES/IMG/2-1-1.jpg'),
  (2, 'Как должен поступить водитель, если на участке дороги значения временных и постоянных знаков противоречат друг другу?', 'Руководствоваться требованиями временных знаков.', 'Руководствоваться требованиями постоянных знаков.', 'Руководствоваться только горизонтальной и вертикальной дорожной разметкой.', NULL, '1', 'Изучите приложение 2 Глава 1 ПДД', '/VIEW/RESOURCES/IMG/2-1-2.jpg'),
  (2, 'Показанный запрещающий дорожный знак с табличкой обязывает водителей соблюдать дистанцию:', 'Не менее 50 метров на протяжении 250 метров.', 'От 50 метров до 250 метров.', 'Менее 50 метров на протяжении 250 метров.', NULL, '1', 'Изучите приложение 2 пар.3 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-3.jpg'),
  (2, 'Разрешен ли Вам обгон в показанной ситуации?', 'Разрешен, если скорость грузового автомобиля более 70 км/ч.', 'Запрещен.', 'Разрешен, если скорость грузового автомобиля не более 70 км/ч.', NULL, '3', 'Изучите приложение 2 пар.3 ПДД', '/VIEW/RESOURCES/IMG/2-1-4.jpg'),
  (2, 'О чем предупреждают водителя показанные дорожные знаки, установленные вне населенного пункта?', 'О наличии через 500 метров двух или более опасных поворотов.', 'О том, что на расстоянии от 150 до 300 метров начнется участок дороги протяженностью 500 метров с тремя или более опасными поворотами.', NULL, NULL, '2', 'Изучите приложение 2 пар.1 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-5.jpg'),
  (2, 'Разрешено ли Вам поставить автомобиль на стоянку в указанном месте?', 'Разрешено только если Вы работаете рядом с этим местом.', 'Разрешено.', 'Запрещено.', NULL, '2', 'Изучите приложение 2 пар.3 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-6.jpg'),
  (2, 'В каком направлении Вам разрешено продолжить движение из занимаемой полосы?', 'Только на лево.', 'Только на лево или на разворот.', NULL, NULL, '1', 'Изучите приложение 3 гл.2 ПДД', '/VIEW/RESOURCES/IMG/2-1-7.jpg'),
  (2, 'Разрешено ли Вам продолжить движение за знаками прямо?', 'Запрещено.', 'Разрешено, потому что перекрыта толькко встречная полоса движения.', NULL, NULL, '1', 'Изучите приложение 2 пар.3 ПДД', '/VIEW/RESOURCES/IMG/2-1-8.jpg'),
  (2, 'О чем предупреждает водителя показанное сочетание дорожных знаков?', 'О проведении дорожных работ по ремонту обочины.', 'О проведении работ по нанесению дорожной разметки.', 'О проведении работ по ремонту поверхности дорожного покрытия.', NULL, '2', 'Изучите приложение 2 пар.1 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-9.jpg'),

  (2, 'Как называется показанный на рисунке дорожный знак?', 'Скользкая дорога.', 'Опасные повороты.', 'Неровная дорога.', 'Аварийно-опасный участок.', '1', 'Изучите приложение 2 пар.1 ПДД', '/VIEW/RESOURCES/IMG/2-1-10.jpg'),
  --3
  (3, 'Намереваясь продолжить движение прямо в показанной ситуации, Вы:', 'Обязаны уступить дорогу автобусу.', 'Пользуясь преимуществом, проедете перекресток первым.', NULL, NULL, '2', 'Изучите п.108 ПДД', '/VIEW/RESOURCES/IMG/3-1-1.jpg'),
  (3, 'В каком направлении Вам разрешено продолжить движение в показанной ситуации?', 'Прямо или направо.', 'Во всех направлениях.', 'Только прямо.', 'Только направо.', '4', 'Изучите п.36.2.2 ПДД', '/VIEW/RESOURCES/IMG/3-1-2.jpg'),
  (3, 'Управляя легковым автомобилем, Вы намерены повернуть направо. Как Вы должны поступить в показанной ситуации, если водитель трамвая намерен продолжить движение прямо?', 'Ожидать другого сигнала регулировщика.', 'Осуществить поворот, пользуясь преимуществом.', 'Осуществить поворот, уступив трамваю.', NULL, '3', 'Изучите п.п.36.1 и 105 ПДД', '/VIEW/RESOURCES/IMG/3-1-3.jpg'),
  (3, 'Намереваясь продолжить движение прямо в показанной ситуации, Вы:', 'Обязаны уступить дорогу трактору.', 'Имеете преимуществом.', NULL, NULL, '2', 'Изучите п.п.2.11, 2.51. 2.75 и 108 ПДД', '/VIEW/RESOURCES/IMG/3-1-4.jpg'),
  (3, 'Кому Вы обязаны уступить дорогу при повороте налево на перекрестке в показанной ситуации?', 'Только легковому автомобилю.', 'Только желтому автобусу.', 'Обоим транспортным средствам.', NULL, '2', 'Изучите ', '/VIEW/RESOURCES/IMG/3-1-5.jpg'),
  (3, 'О чем информируют Вас стрелки на основном зеленом сигнале светофора?', 'Движение направо регулируется дополнительной секцией.', 'На этом перекрестке всегда запрещен поворот направо.', NULL, NULL, '1', 'Изучите п.41 ПДД ', '/VIEW/RESOURCES/IMG/3-1-6.jpg'),
  (3, 'Обязаны ли водители исполнять сигналы регулировщика, если они противоречат сигналам светофора?', 'Обязаны.', 'Не обязаны.', NULL, NULL, '1', 'Изучите п.35 ПДД', '/VIEW/RESOURCES/IMG/3-1-7.jpg'),
  (3, 'Что означает информационная табличка, расположенная под секцией светофора?', 'Предупреждает о расположении пешеходной дорожки справа от перекрестка.', 'Предупреждает о наличии нескольких пешеходных переходов на участке дороги, на которую совершается поворот направо.', 'Дополнительно предупреждает водителей о необходимости уступить дорогу пешеходам при повороте направо.', NULL, '3', 'Изучите п.113 ПДД', '/VIEW/RESOURCES/IMG/3-1-8.jpg'),
  (3, 'Разрешено ли продолжить движение транспортным средствам в показанной ситуации?', 'Запрещено движение обоим автомобилям.', 'Разрешено только зеленому автомобилю.', 'Разрешено обоим.', 'Разрешено только красному.', '1', 'Изучите п.36.3 ПДД', '/VIEW/RESOURCES/IMG/3-1-9.jpg'),
  (3, 'При повороте налево на показанном равнозначном перекрестке Вы', 'Имеете преимущество.', 'Обязаны уступить дорогу автобусу.', NULL, NULL, '2', 'Изучите п.110 ПДД', '/VIEW/RESOURCES/IMG/3-1-10.jpg'),

  --4
  (4, 'Разрешено ли Вам остановиться в показанной ситуации?', 'Разрешено.', 'Запрещено.', NULL, NULL, '2', 'Изучите п.143.18 ПДД', '/VIEW/RESOURCES/IMG/4-1-1.jpg'),
  (4, 'Разрешено ли Вам поставить автомобиль на стоянку на обочине дороги с односторонним движением вне населенного пункта в показанной ситуации?', 'Разрешено только на правой обочине.', 'Разрешено как на правой обочине, так и на левой.', 'Запрещено как на правой обочине, так и на левой.', 'Разрешено только на левой.', '3', 'Изучите п.145.1 ПДД', '/VIEW/RESOURCES/IMG/4-1-2.jpg'),
  (4, 'Разрешено ли Вам выполнить обгон трактора в показанной ситуации?', 'Разрешено, если обгон будет завершен до железнодорожного переезда.', 'Разрешено.', 'Запрещено.', 'Разрешено, если обгон будет завершен за 100 метров до железнодорожного переезда.', '4', 'Изучите п.96.7 ПДД и приложение 2 пар.1 ПДД', '/VIEW/RESOURCES/IMG/4-1-3.jpg'),
  (4, 'Разрешено ли Вам остановиться на обочине под мостом в показанной ситуации?', 'Запрещено.', 'Разрешено.', NULL, NULL, '1', 'Изучите п.143.6 ПДД', '/VIEW/RESOURCES/IMG/4-1-4.jpg'),
  (4, 'Кто из водителей должен уступить дорогу при одновременном перестроении?', 'Водитель легкового автомобиля.', 'Водитель грузового автомобиля.', NULL, NULL, '2', 'Изучите п.59 ПДД', '/VIEW/RESOURCES/IMG/4-1-5.jpg'),
  (4, 'Завершив опережение в показанной ситуации при отсутствии дорожных знаков, запрещающих движение велосипедистов и гужевых транспортных средств, и свободной правой полосе, Вы:', 'Можете вести автомобиль по любой полосе движения.', 'Должны вести автомобиль по возможности ближе к правому краю проезжей части дороги.', 'Должны вести автомобиль по возможности ближе к левому краю в пределах правой крайней полосы движения.', NULL, '3', 'Изучите п.п.78 и 79.1 ПДД', '/VIEW/RESOURCES/IMG/4-1-6.jpg'),
  (4, 'Разрешено ли Вам выполнить опережение грузового автомобиля на обозначенном перекрестке в показанной ситуации?', 'Разрещено.', 'Запрещено.', NULL, NULL, '1', 'Изучите п.п.2.32, 2.38 и 96.2 ПДД', '/VIEW/RESOURCES/IMG/4-1-7.jpg'),
  (4, 'С какой максимальной скоростью Вам, при управлении грузовым автомобилем с технически допустимой общей массой менее 3,5 тонны, разрешено продолжить движение на показанном участке дороги за дорожным знаком в населенном пункте?', '40 км/ч.', '50 км/ч.', '60 км/ч.', NULL, '3', 'Изучите п.88 и приложение 2 пар.3 ПДД', '/VIEW/RESOURCES/IMG/4-1-8.jpg'),
  (4, 'Разрешается ли движение задним ходом на показанном участке дороги?', 'Запрещается.', 'Разрешается.', NULL, NULL, '2', 'Изучите п.74 и 2 пар.5 ПДД', '/VIEW/RESOURCES/IMG/4-1-9.jpg'),
  (4, 'На каком минимальном расстоянии от железнодорожного переезда разрешена остановка транспортных средств?', '15 метров.', '20 метров.', '40 метров.', '50 метров.', '4', 'Изучите п.143.8 ПДД', '/VIEW/RESOURCES/IMG/4-1-10.jpg'),
  --5
  (5, 'Что в первую очередь должен предпринять водитель при вынужденной остановке на железнодорожном переезде?', 'Принять меры для освобождения железнодорожного переезда.', 'Подавать сигнал общей тревоги.', 'Немедленно высадить пассажиров.', 'Включить аварийную световую сигнализацию.', '4', 'Изучите п.130 ПДД', '/VIEW/RESOURCES/IMG/5-1-1.jpg'),
  (5, 'Разрешено ли водителю поставить автомобиль на стоянку на освещенном участке дороги в населенном пункте не в жилой зоне в условиях недостаточной видимости, не включив габаритные или стояночные огни?', 'Разрещено.', 'Запрещено.', NULL, NULL, '2', 'Изучите п.164 ПДД', NULL),
  (5, 'С какого возраста разрешается перевозка детей на заднем сиденье мопеда?', 'С 8 лет.', 'С 10 лет.', 'С 12 лет.', 'С 14 лет.', '3', 'Изучите п.181.5 ПДД', NULL),
  (5, 'Как Вам следует поступить при управлении автомобилем в показанной ситуации?', 'Уступить дорогу трамваю, выезжающему из депо.', 'Пользуясь преимуществом продолжить движение, не уступая дороги трамваю.', NULL, NULL, '2', 'Изучите п.123 ПДД', '/VIEW/RESOURCES/IMG/5-1-4.jpg'),
  (5, 'Как должно быть обозначено транспортное средство при остановке в темное время суток на неосвещенном участке дороги вне населенного пункта?', 'Включенной фарой-прожектором.', 'Включенными задними противотуманными фонарями.', 'Включенными габаритными или стояночными огнями.', 'Включение внешних световых приборов в данной ситуации необязательно.', '3', 'Изучите п.164 ПДД', NULL),
  (5, 'Что может означать включение аварийной световой сигнализации водителем попутно движущегося автомобиля в темное время суток?', 'Ослепление водителя дальним или ближним светом фар.', 'Просьбу о переключении дальнего света фар на ближний.', 'Все вышеперечисленное.', NULL, '3', 'Изучите п.п.163.3 и 163.4 ПДД', NULL),
  (5, 'Разрешается ли Вам буксировать автомобиль на жесткой сцепке с неисправной рабочей тормозной системой, если масса этого автомобиля превышает половину массы Вашего автомобиля?', 'Разрешается только при скорости буксировки не более 30км/ч.', 'Разрешается.', 'Запрещается.', NULL, '3', 'Изучите п.192.2 ПДД', NULL),
  (5, 'Кто пользуется преимуществом в показанной ситуации?', 'Велосипедист.', 'Вы, управляя легковым автомобилем.', NULL, NULL, '2', 'Изучите п.150 ПДД', '/VIEW/RESOURCES/IMG/5-1-8.jpg'),
  (5, 'Разрешено ли Вам при управлении легковым автомобилем двигаться со скоростью менее 40 км/ч в показанной ситуации за дорожным знаком?', 'Запрещено, если Ваш автомобиль по техническим причинам не может развивать скорость движения более 40 км/ч.', 'Запрещено во всех случаях.', 'Разрешено во всех случаях.', NULL, '1', 'Изучите п.131.1 ПДД', '/VIEW/RESOURCES/IMG/5-1-9.jpg'),
  (5, 'Как должен выезжать из дворовой территории на дорогу водитель гужевого транспортного средства в темное время суток?', 'Может вести животное под уздцы в случае, если на дороге, на которую он выезжает, идет оживленное движение механических транспортных средств.', 'По общим правилам, убедившись в безопасности движения.', 'Обязан вести животное под уздцы.', NULL, '3', 'Изучите п.161 ПДД', NULL),
  --6
  (6, 'Какая минимальная степень светопропускания допускается Правилами для передних боковых стекол, при которой не запрещается участие в дорожном движении транспортного средства?', '85 процентов.', '80 процентов.', '75 процентов.', '70 процентов.', '4', 'Изучите п.194.12 ПДД', NULL),
  (6, 'Участие в дорожном движении транспортного средства разрешается, если его передние боковые стекла имеют степень светопропускания:', 'Менее 70%.', 'Не менее 70%.', NULL, NULL, '2', 'Изучите п.194.12 ПДД', NULL),
  (6, 'Должны ли Вы на механическом транспортном средстве устанавливать "Отличительный знак транспортного средства Республики Беларусь"?', 'Можете не устанавливать, если траснпортное средство эксплуатируется только на дорогах Республики Беларусь.', 'Должны устанавливать во всех случаях.', 'Можете не устанавливать, если на регистрационном знаке содержится изображение символа "BY".', NULL, '3', 'Изучите п.201.9 ПДД', '/VIEW/RESOURCES/IMG/6-1-3.jpg'),
  (6, 'В каком из перечисленных случаев не разрешается участие в дорожном движении легкового автомобиля?', 'Имеются значительные внешние повреждения деталей кузова.', 'Имеются значительные внешние повреждения окраски автомобиля.', 'Окраска автомобиля не соответствует указанной в свидетельстве о регистрации транспортного средства.', 'В любом из перечисленных случаев легковому автомобилю не разрешается участвовать в дорожном движении.', '4', 'Изучите приложение 4 гл.7 ПДД', NULL),
  (6, 'В каком случае из перечисленных может устанавливаться показанный опознавательный знак?', 'На автомобиль, который принадлежит инвалиду I группы, но он не управляется им и автомобиль не перевозит такого инвалида.', 'На автомобиль, оборудованный ручным управлением.', 'На автомобиль, управляемый водителем-инвалидом по слуху.', 'На автомобиль, перевозящий инвалидов I или II группы.', '4', 'Изучите приложение п.203.1 ПДД', '/VIEW/RESOURCES/IMG/6-1-5.jpg'),
  (6, 'При возникновении какой неисправности Вам запрещено продолжить дальнейшее движение даже до места ремонта или стоянки?', 'Неисправна система выпуска отработавших газов.', 'Неисправна рабочая тормозная система.', 'Не работает стеклоомыватель ветрового стекла.', NULL, '2', 'Изучите п.п.194.8 и 196 ПДД', NULL),
  (6, 'Что означает опознавательный знак, установленный на грузовом автомобиле в показанной ситуации?', 'Транспортное средство, движущееся впереди, большой грузоподъемности.', 'Транспортное средство, движущееся впереди, большой длины.', 'Впереди движущееся транспортное средство движется в транспортной колонне.', NULL, '3', 'Изучите приложение 5 и п.201.7 ПДД', '/VIEW/RESOURCES/IMG/6-1-7.jpg'),
  (6, 'В каком из перечисленных мест на автомобиле, имеющем ошипованные шины, должен быть установлен показанный опознавательный знак?', 'Может устанавливаться в любом месте.', 'Спереди и сзади автомобиля.', 'Только сзади автомобиля.', NULL, '3', 'Изучите приложение 5 и п.201.3 ПДД', '/VIEW/RESOURCES/IMG/6-1-8.jpg'),
  (6, 'При какой неисправности тормозной системы Вам запрещается участие в дорожном движении на легковом автомобиле?', 'Не включается контррльная лампа стояночной тормозной системы.', 'Уменьшен свободный ход педали тормоза.', 'Запрещается во всех перечисленных.', 'Стояночная тормозная система не обеспечивает неподвижное состояние транспортного средства с полной нагрузкой на уклоне до 16 % включительно.', '4', 'Изучите приложение 4 гл.1 ПДД', NULL),
  (6, 'Разрешается ли участие в дорожном движении механических транспортных средств без установленных в предусмотренных местах регистрационных знаков(временных номерных знаков)?', 'Разрешается в период проведения регистрационных действий, порядок которых определен Советом Министров Республики Беларусь.', 'Запрещается.', NULL, NULL, '2', 'Изучите п.194.2 ПДД', '/VIEW/RESOURCES/IMG/6-1-10.jpg'),
  --7
  (7, 'В чем из перечисленного заключается опасность длительного торможения автомобиля с выключенной передачей на крутых затяжных спусках?', 'В повышенном износе деталей сцепления.', 'В повышенном износе шин.', 'В повышенном износе деталей двигателя.', 'В перегреве тормозов и отказ их в работе.', '4', NULL, '/VIEW/RESOURCES/IMG/7-1-1.jpg'),
  (7, 'Для чего применяется 5%-ный спиртовой раствор йода, входящий в комплект медицинской аптечки, которой оснащены механические транспортные средства?', 'Как средство, успокаивающее боли в области сердца.', 'Для вдыхания при обмороке, угаре.', 'Для обработки края раны при повреждении кожи и тканей.', NULL, '3', NULL, '/VIEW/RESOURCES/IMG/7-1-2.jpg'),
  (7, 'В каком случае из перечисленных транспортировка пострадавших осуществляется сидя?', 'При черепно-мозговой травме.', 'При наличии повреждений верхних конечностей, органов брюшной полости.', 'При переломе ключицы, переломе ребер.', NULL, '3', NULL, NULL),
  (7, 'Как правильно транспортировать пострадавшего с переломом ключицы?', 'Лежа на животе.', 'Лежа на спине.', 'Сидя.', 'Лежа на боку.', '3', NULL, NULL),
  (7, 'Что следует сделать водителю, чтобы предотвратить возникновение заноса при поезде крутого поворота при управлении автомобилем с механической коробкой передач?', 'Перед поворотом снизить скорость, при необходимости включить пониженную передачу, а при проезде поворота не увеличивать резко скорость и не тормозить.', 'Перед поворотом снизить скорость и выжать педаль сцепления, чтобы дать возможность автомобилю двигатья накатом на повороте.', 'Допускается любое из перечисленных действий.', NULL, '1', NULL, '/VIEW/RESOURCES/IMG/7-1-5.jpg'),
  (7, 'Как остановить кровотечение при ранении артерии с использованием вложений, входящих в состав автомобильной аптечки?', 'Наложить жгут выше места ранения и под жгутом обязательно оставить записку со временем его наложения. В любое время года ослаблять жгут на 10-15 минут не реже чем раз в 30-40 минут от момента его наложения.', 'Наложить жгут ниже места ранения.', NULL, NULL, '1', NULL, '/VIEW/RESOURCES/IMG/7-1-6.jpg'),
  (7, 'Для экстренной остановки автомобиля с механической коробкой передач на сухой дороге необходимо:', 'Выключить передачу и резко нажать на педаль тормоза.', 'Выключить сцепление, после чего незамедлительно нажать на педаль тормоза.', 'Нажать на педаль тормоза, не выключая сцепления.', NULL, '3', NULL, NULL),
  (7, 'Водитель может быть признан виновным в совершении дорожно-транспортного происшествия и подвергнут уголовному наказанию по решению:', 'Государственной автомобильной инспекции.', 'Органов предварительного расследования.', 'Суда.', 'Органов прокуратуры.', '3', NULL, NULL),
  (7, 'Как изменяется устойчивость автомобиля на подъеме и косогоре с повышением высоты центра тяжести?', 'Не меняется.', 'Увеличивается.', 'Уменьшается.', NULL, '3', NULL, NULL),
  (7, 'Что следует сделать в первую очередь для оказания помощи лежащему на земле пострадавшему без видимых наружных повреждений, но находящемуся в бессознательном состоянии?', 'Поднести к носу ватку с нашатырным спиртом, приподнять голову, дать питье.', 'Запрокинуть голову на затылок, открыть рот, при необходимости очистить ротовую полость от инородных масс, контролировать наличие дыхания и пульса, срочно вызвать "Скорую медицинскую помощь".', 'Подложить под ноги валик, срочно вызвать "Скорую медицинскую помощь".', NULL, '2', NULL, NULL),
  (0, 'Как следует поступить водителю желтого легкового автомобиля в показанной ситуации?', 'Остановиться только на левой обочине (А)', 'Остановиться только на правой обочине (Б)', 'Остановиться на правой (Б) или на левой (А) обочине', NULL, -- Answer_4
  '3', --Right_Answer
  'Изучите п.26 ПДД', -- Paragraph
  '/VIEW/RESOURCES/IMG/1-1-1.jpg' -- Image
  ),
  (0, 'Согласно Правилам, водителю запрещается учавствовать в движении на транспортном средстве с загрязнениями, не позволяющими видеть информацию на регистрационном знаке с расстояния:', '100 метров и менее.', '50 метров и менее.', '40 метров и менее.', '10 метров и менее.', '3', 'Изучите п.10.3 ПДД', '/VIEW/RESOURCES/IMG/1-1-2.jpg'),
  (0, 'Какое из перечисленных дорожных покрытий является усовершенствованным?', 'Покрытие из асфальтобетонных смесей.', 'Покрытие из булыжника.', 'Покрытие из брусчатки.', 'Все перечисленные дорожные покрытия являются усовершенствованными.', '4', -- Right_Answer
  'Изучите п.2.75 ПДД', -- Paragraph
  '/VIEW/RESOURCES/IMG/1-1-3.jpg' -- Image
  ),
  (0, 'В каких случаях водителю не запрещено управлять транспортным средством?', 'Если он находится под воздействием лекарственных препаратов, повышающих быстроту реакции и внимания.', 'Если он пользуется во время движения телефонной связью, при отсутствии устройства, позволяющего вести переговоры без использования рук.', 'Если он находится в утомленном состоянии, ставящем под угрозу безопасность дорожного движения.', 'Во всех случаях запрещено', '1', 'Изучите п.10 ПДД', NULL),
  (0, 'Является ли островком безопасности участок пешеходного перехода, проходящий через трамвайные пути?', 'Является.', 'Не является.', NULL, NULL, '2', 'Изучите п.2.42 ПДД', '/VIEW/RESOURCES/IMG/1-1-5.jpg'),
  (0, 'Кто из перечисленных лиц при управлении механическим транспортным средством, согласно ПДД, является водителем?', 'Лицо, сдающее квалификационный практический экзамен на право управления механическим транспортным средством.', 'Лицо, обучаемое управлению механическим транспортным средством.', 'Лицо, управляющее транспортным средством, не имеющее водительского удостоверения.', 'Все перечисленные являются водителями', '3', 'Изучите п.2.8 ПДД', NULL),
  (0, 'Разрешено ли пассажиру при поездке на мотоцикле быть без застегнутого мотошлема?', 'Разрешено при поездке ребенка до семи лет в боковом прицепе.', 'Запрещено.', 'Разрешено при поездке в боковом прицепе.', NULL, '2', 'Изучите п.9.5 ПДД', NULL),
  (0, 'Делает ли дорогу без усовершенствованного покрытия наличие участка с усовершенствованным покрытием непосредственно перед перекрестком равной по значению с пересекаемой дорогой с усовершенствованным покрытием?', 'Не делает.', 'Делает', NULL, NULL, '1', 'Изучите п.2.11 ПДД', '/VIEW/RESOURCES/IMG/1-1-8.jpg'),
  (0, 'В показанной ситуации Вы при приближении встречного транспортного средства оперативного назначения с включенными маячками синего цвета:', 'Обязаны остановиться справа у тротуара.', 'Можете продолжать движение, уступая дорогу и обеспечивая беспрепятственный проезд транспортному средству оперативного назначения.', NULL, NULL, '2', 'Изучите п.26 ПДД', '/VIEW/RESOURCES/IMG/1-1-9.jpg'),
  (0, 'Имеет ли право водитель на возмещение причиненного ему или его имуществу вреда в порядке, установленном законодательством?', 'Имеет в любом случае.', 'Имеет, только если его автомобиль застрахован.', NULL, NULL, '1', 'Изучите п.6.5 ПДД', NULL),
  --2
  (0, 'Какие знаки из показанных устанавливают непосредственно перед железнодорожным переездом?', 'Только знаки 1 и 3.', 'Только знак 1.', 'Только знак 2.', 'Только знак 3', '4', 'Изучите приложение 2 пар.1 ПДД', '/VIEW/RESOURCES/IMG/2-1-1.jpg'),
  (0, 'Как должен поступить водитель, если на участке дороги значения временных и постоянных знаков противоречат друг другу?', 'Руководствоваться требованиями временных знаков.', 'Руководствоваться требованиями постоянных знаков.', 'Руководствоваться только горизонтальной и вертикальной дорожной разметкой.', NULL, '1', 'Изучите приложение 2 Глава 1 ПДД', '/VIEW/RESOURCES/IMG/2-1-2.jpg'),
  (0, 'Показанный запрещающий дорожный знак с табличкой обязывает водителей соблюдать дистанцию:', 'Не менее 50 метров на протяжении 250 метров.', 'От 50 метров до 250 метров.', 'Менее 50 метров на протяжении 250 метров.', NULL, '1', 'Изучите приложение 2 пар.3 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-3.jpg'),
  (0, 'Разрешен ли Вам обгон в показанной ситуации?', 'Разрешен, если скорость грузового автомобиля более 70 км/ч.', 'Запрещен.', 'Разрешен, если скорость грузового автомобиля не более 70 км/ч.', NULL, '3', 'Изучите приложение 2 пар.3 ПДД', '/VIEW/RESOURCES/IMG/2-1-4.jpg'),
  (0, 'О чем предупреждают водителя показанные дорожные знаки, установленные вне населенного пункта?', 'О наличии через 500 метров двух или более опасных поворотов.', 'О том, что на расстоянии от 150 до 300 метров начнется участок дороги протяженностью 500 метров с тремя или более опасными поворотами.', NULL, NULL, '2', 'Изучите приложение 2 пар.1 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-5.jpg'),
  (0, 'Разрешено ли Вам поставить автомобиль на стоянку в указанном месте?', 'Разрешено только если Вы работаете рядом с этим местом.', 'Разрешено.', 'Запрещено.', NULL, '2', 'Изучите приложение 2 пар.3 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-6.jpg'),
  (0, 'В каком направлении Вам разрешено продолжить движение из занимаемой полосы?', 'Только на лево.', 'Только на лево или на разворот.', NULL, NULL, '1', 'Изучите приложение 3 гл.2 ПДД', '/VIEW/RESOURCES/IMG/2-1-7.jpg'),
  (0, 'Разрешено ли Вам продолжить движение за знаками прямо?', 'Запрещено.', 'Разрешено, потому что перекрыта толькко встречная полоса движения.', NULL, NULL, '1', 'Изучите приложение 2 пар.3 ПДД', '/VIEW/RESOURCES/IMG/2-1-8.jpg'),
  (0, 'О чем предупреждает водителя показанное сочетание дорожных знаков?', 'О проведении дорожных работ по ремонту обочины.', 'О проведении работ по нанесению дорожной разметки.', 'О проведении работ по ремонту поверхности дорожного покрытия.', NULL, '2', 'Изучите приложение 2 пар.1 и 7 ПДД', '/VIEW/RESOURCES/IMG/2-1-9.jpg'),

  (0, 'Как называется показанный на рисунке дорожный знак?', 'Скользкая дорога.', 'Опасные повороты.', 'Неровная дорога.', 'Аварийно-опасный участок.', '1', 'Изучите приложение 2 пар.1 ПДД', '/VIEW/RESOURCES/IMG/2-1-10.jpg'),
  --3
  (0, 'Намереваясь продолжить движение прямо в показанной ситуации, Вы:', 'Обязаны уступить дорогу автобусу.', 'Пользуясь преимуществом, проедете перекресток первым.', NULL, NULL, '2', 'Изучите п.108 ПДД', '/VIEW/RESOURCES/IMG/3-1-1.jpg'),
  (0, 'В каком направлении Вам разрешено продолжить движение в показанной ситуации?', 'Прямо или направо.', 'Во всех направлениях.', 'Только прямо.', 'Только направо.', '4', 'Изучите п.36.2.2 ПДД', '/VIEW/RESOURCES/IMG/3-1-2.jpg'),
  (0, 'Управляя легковым автомобилем, Вы намерены повернуть направо. Как Вы должны поступить в показанной ситуации, если водитель трамвая намерен продолжить движение прямо?', 'Ожидать другого сигнала регулировщика.', 'Осуществить поворот, пользуясь преимуществом.', 'Осуществить поворот, уступив трамваю.', NULL, '3', 'Изучите п.п.36.1 и 105 ПДД', '/VIEW/RESOURCES/IMG/3-1-3.jpg'),
  (0, 'Намереваясь продолжить движение прямо в показанной ситуации, Вы:', 'Обязаны уступить дорогу трактору.', 'Имеете преимуществом.', NULL, NULL, '2', 'Изучите п.п.2.11, 2.51. 2.75 и 108 ПДД', '/VIEW/RESOURCES/IMG/3-1-4.jpg'),
  (0, 'Кому Вы обязаны уступить дорогу при повороте налево на перекрестке в показанной ситуации?', 'Только легковому автомобилю.', 'Только желтому автобусу.', 'Обоим транспортным средствам.', NULL, '2', 'Изучите ', '/VIEW/RESOURCES/IMG/3-1-5.jpg'),
  (0, 'О чем информируют Вас стрелки на основном зеленом сигнале светофора?', 'Движение направо регулируется дополнительной секцией.', 'На этом перекрестке всегда запрещен поворот направо.', NULL, NULL, '1', 'Изучите п.41 ПДД ', '/VIEW/RESOURCES/IMG/3-1-6.jpg'),
  (0, 'Обязаны ли водители исполнять сигналы регулировщика, если они противоречат сигналам светофора?', 'Обязаны.', 'Не обязаны.', NULL, NULL, '1', 'Изучите п.35 ПДД', '/VIEW/RESOURCES/IMG/3-1-7.jpg'),
  (0, 'Что означает информационная табличка, расположенная под секцией светофора?', 'Предупреждает о расположении пешеходной дорожки справа от перекрестка.', 'Предупреждает о наличии нескольких пешеходных переходов на участке дороги, на которую совершается поворот направо.', 'Дополнительно предупреждает водителей о необходимости уступить дорогу пешеходам при повороте направо.', NULL, '3', 'Изучите п.113 ПДД', '/VIEW/RESOURCES/IMG/3-1-8.jpg'),
  (0, 'Разрешено ли продолжить движение транспортным средствам в показанной ситуации?', 'Запрещено движение обоим автомобилям.', 'Разрешено только зеленому автомобилю.', 'Разрешено обоим.', 'Разрешено только красному.', '1', 'Изучите п.36.3 ПДД', '/VIEW/RESOURCES/IMG/3-1-9.jpg'),
  (0, 'При повороте налево на показанном равнозначном перекрестке Вы', 'Имеете преимущество.', 'Обязаны уступить дорогу автобусу.', NULL, NULL, '2', 'Изучите п.110 ПДД', '/VIEW/RESOURCES/IMG/3-1-10.jpg'),

  --4
  (0, 'Разрешено ли Вам остановиться в показанной ситуации?', 'Разрешено.', 'Запрещено.', NULL, NULL, '2', 'Изучите п.143.18 ПДД', '/VIEW/RESOURCES/IMG/4-1-1.jpg'),
  (0, 'Разрешено ли Вам поставить автомобиль на стоянку на обочине дороги с односторонним движением вне населенного пункта в показанной ситуации?', 'Разрешено только на правой обочине.', 'Разрешено как на правой обочине, так и на левой.', 'Запрещено как на правой обочине, так и на левой.', 'Разрешено только на левой.', '3', 'Изучите п.145.1 ПДД', '/VIEW/RESOURCES/IMG/4-1-2.jpg'),
  (0, 'Разрешено ли Вам выполнить обгон трактора в показанной ситуации?', 'Разрешено, если обгон будет завершен до железнодорожного переезда.', 'Разрешено.', 'Запрещено.', 'Разрешено, если обгон будет завершен за 100 метров до железнодорожного переезда.', '4', 'Изучите п.96.7 ПДД и приложение 2 пар.1 ПДД', '/VIEW/RESOURCES/IMG/4-1-3.jpg'),
  (0, 'Разрешено ли Вам остановиться на обочине под мостом в показанной ситуации?', 'Запрещено.', 'Разрешено.', NULL, NULL, '1', 'Изучите п.143.6 ПДД', '/VIEW/RESOURCES/IMG/4-1-4.jpg'),
  (0, 'Кто из водителей должен уступить дорогу при одновременном перестроении?', 'Водитель легкового автомобиля.', 'Водитель грузового автомобиля.', NULL, NULL, '2', 'Изучите п.59 ПДД', '/VIEW/RESOURCES/IMG/4-1-5.jpg'),
  (0, 'Завершив опережение в показанной ситуации при отсутствии дорожных знаков, запрещающих движение велосипедистов и гужевых транспортных средств, и свободной правой полосе, Вы:', 'Можете вести автомобиль по любой полосе движения.', 'Должны вести автомобиль по возможности ближе к правому краю проезжей части дороги.', 'Должны вести автомобиль по возможности ближе к левому краю в пределах правой крайней полосы движения.', NULL, '3', 'Изучите п.п.78 и 79.1 ПДД', '/VIEW/RESOURCES/IMG/4-1-6.jpg'),
  (0, 'Разрешено ли Вам выполнить опережение грузового автомобиля на обозначенном перекрестке в показанной ситуации?', 'Разрещено.', 'Запрещено.', NULL, NULL, '1', 'Изучите п.п.2.32, 2.38 и 96.2 ПДД', '/VIEW/RESOURCES/IMG/4-1-7.jpg'),
  (0, 'С какой максимальной скоростью Вам, при управлении грузовым автомобилем с технически допустимой общей массой менее 3,5 тонны, разрешено продолжить движение на показанном участке дороги за дорожным знаком в населенном пункте?', '40 км/ч.', '50 км/ч.', '60 км/ч.', NULL, '3', 'Изучите п.88 и приложение 2 пар.3 ПДД', '/VIEW/RESOURCES/IMG/4-1-8.jpg'),
  (0, 'Разрешается ли движение задним ходом на показанном участке дороги?', 'Запрещается.', 'Разрешается.', NULL, NULL, '2', 'Изучите п.74 и 2 пар.5 ПДД', '/VIEW/RESOURCES/IMG/4-1-9.jpg'),
  (0, 'На каком минимальном расстоянии от железнодорожного переезда разрешена остановка транспортных средств?', '15 метров.', '20 метров.', '40 метров.', '50 метров.', '4', 'Изучите п.143.8 ПДД', '/VIEW/RESOURCES/IMG/4-1-10.jpg'),
  --5
  (0, 'Что в первую очередь должен предпринять водитель при вынужденной остановке на железнодорожном переезде?', 'Принять меры для освобождения железнодорожного переезда.', 'Подавать сигнал общей тревоги.', 'Немедленно высадить пассажиров.', 'Включить аварийную световую сигнализацию.', '4', 'Изучите п.130 ПДД', '/VIEW/RESOURCES/IMG/5-1-1.jpg'),
  (0, 'Разрешено ли водителю поставить автомобиль на стоянку на освещенном участке дороги в населенном пункте не в жилой зоне в условиях недостаточной видимости, не включив габаритные или стояночные огни?', 'Разрещено.', 'Запрещено.', NULL, NULL, '2', 'Изучите п.164 ПДД', NULL),
  (0, 'С какого возраста разрешается перевозка детей на заднем сиденье мопеда?', 'С 8 лет.', 'С 10 лет.', 'С 12 лет.', 'С 14 лет.', '3', 'Изучите п.181.5 ПДД', NULL),
  (0, 'Как Вам следует поступить при управлении автомобилем в показанной ситуации?', 'Уступить дорогу трамваю, выезжающему из депо.', 'Пользуясь преимуществом продолжить движение, не уступая дороги трамваю.', NULL, NULL, '2', 'Изучите п.123 ПДД', '/VIEW/RESOURCES/IMG/5-1-4.jpg'),
  (0, 'Как должно быть обозначено транспортное средство при остановке в темное время суток на неосвещенном участке дороги вне населенного пункта?', 'Включенной фарой-прожектором.', 'Включенными задними противотуманными фонарями.', 'Включенными габаритными или стояночными огнями.', 'Включение внешних световых приборов в данной ситуации необязательно.', '3', 'Изучите п.164 ПДД', NULL),
  (0, 'Что может означать включение аварийной световой сигнализации водителем попутно движущегося автомобиля в темное время суток?', 'Ослепление водителя дальним или ближним светом фар.', 'Просьбу о переключении дальнего света фар на ближний.', 'Все вышеперечисленное.', NULL, '3', 'Изучите п.п.163.3 и 163.4 ПДД', NULL),
  (0, 'Разрешается ли Вам буксировать автомобиль на жесткой сцепке с неисправной рабочей тормозной системой, если масса этого автомобиля превышает половину массы Вашего автомобиля?', 'Разрешается только при скорости буксировки не более 30км/ч.', 'Разрешается.', 'Запрещается.', NULL, '3', 'Изучите п.192.2 ПДД', NULL),
  (0, 'Кто пользуется преимуществом в показанной ситуации?', 'Велосипедист.', 'Вы, управляя легковым автомобилем.', NULL, NULL, '2', 'Изучите п.150 ПДД', '/VIEW/RESOURCES/IMG/5-1-8.jpg'),
  (0, 'Разрешено ли Вам при управлении легковым автомобилем двигаться со скоростью менее 40 км/ч в показанной ситуации за дорожным знаком?', 'Запрещено, если Ваш автомобиль по техническим причинам не может развивать скорость движения более 40 км/ч.', 'Запрещено во всех случаях.', 'Разрешено во всех случаях.', NULL, '1', 'Изучите п.131.1 ПДД', '/VIEW/RESOURCES/IMG/5-1-9.jpg'),
  (0, 'Как должен выезжать из дворовой территории на дорогу водитель гужевого транспортного средства в темное время суток?', 'Может вести животное под уздцы в случае, если на дороге, на которую он выезжает, идет оживленное движение механических транспортных средств.', 'По общим правилам, убедившись в безопасности движения.', 'Обязан вести животное под уздцы.', NULL, '3', 'Изучите п.161 ПДД', NULL),
  --6
  (0, 'Какая минимальная степень светопропускания допускается Правилами для передних боковых стекол, при которой не запрещается участие в дорожном движении транспортного средства?', '85 процентов.', '80 процентов.', '75 процентов.', '70 процентов.', '4', 'Изучите п.194.12 ПДД', NULL),
  (0, 'Участие в дорожном движении транспортного средства разрешается, если его передние боковые стекла имеют степень светопропускания:', 'Менее 70%.', 'Не менее 70%.', NULL, NULL, '2', 'Изучите п.194.12 ПДД', NULL),
  (0, 'Должны ли Вы на механическом транспортном средстве устанавливать "Отличительный знак транспортного средства Республики Беларусь"?', 'Можете не устанавливать, если траснпортное средство эксплуатируется только на дорогах Республики Беларусь.', 'Должны устанавливать во всех случаях.', 'Можете не устанавливать, если на регистрационном знаке содержится изображение символа "BY".', NULL, '3', 'Изучите п.201.9 ПДД', '/VIEW/RESOURCES/IMG/6-1-3.jpg'),
  (0, 'В каком из перечисленных случаев не разрешается участие в дорожном движении легкового автомобиля?', 'Имеются значительные внешние повреждения деталей кузова.', 'Имеются значительные внешние повреждения окраски автомобиля.', 'Окраска автомобиля не соответствует указанной в свидетельстве о регистрации транспортного средства.', 'В любом из перечисленных случаев легковому автомобилю не разрешается участвовать в дорожном движении.', '4', 'Изучите приложение 4 гл.7 ПДД', NULL),
  (0, 'В каком случае из перечисленных может устанавливаться показанный опознавательный знак?', 'На автомобиль, который принадлежит инвалиду I группы, но он не управляется им и автомобиль не перевозит такого инвалида.', 'На автомобиль, оборудованный ручным управлением.', 'На автомобиль, управляемый водителем-инвалидом по слуху.', 'На автомобиль, перевозящий инвалидов I или II группы.', '4', 'Изучите приложение п.203.1 ПДД', '/VIEW/RESOURCES/IMG/6-1-5.jpg'),
  (0, 'При возникновении какой неисправности Вам запрещено продолжить дальнейшее движение даже до места ремонта или стоянки?', 'Неисправна система выпуска отработавших газов.', 'Неисправна рабочая тормозная система.', 'Не работает стеклоомыватель ветрового стекла.', NULL, '2', 'Изучите п.п.194.8 и 196 ПДД', NULL),
  (0, 'Что означает опознавательный знак, установленный на грузовом автомобиле в показанной ситуации?', 'Транспортное средство, движущееся впереди, большой грузоподъемности.', 'Транспортное средство, движущееся впереди, большой длины.', 'Впереди движущееся транспортное средство движется в транспортной колонне.', NULL, '3', 'Изучите приложение 5 и п.201.7 ПДД', '/VIEW/RESOURCES/IMG/6-1-7.jpg'),
  (0, 'В каком из перечисленных мест на автомобиле, имеющем ошипованные шины, должен быть установлен показанный опознавательный знак?', 'Может устанавливаться в любом месте.', 'Спереди и сзади автомобиля.', 'Только сзади автомобиля.', NULL, '3', 'Изучите приложение 5 и п.201.3 ПДД', '/VIEW/RESOURCES/IMG/6-1-8.jpg'),
  (0, 'При какой неисправности тормозной системы Вам запрещается участие в дорожном движении на легковом автомобиле?', 'Не включается контррльная лампа стояночной тормозной системы.', 'Уменьшен свободный ход педали тормоза.', 'Запрещается во всех перечисленных.', 'Стояночная тормозная система не обеспечивает неподвижное состояние транспортного средства с полной нагрузкой на уклоне до 16 % включительно.', '4', 'Изучите приложение 4 гл.1 ПДД', NULL),
  (0, 'Разрешается ли участие в дорожном движении механических транспортных средств без установленных в предусмотренных местах регистрационных знаков(временных номерных знаков)?', 'Разрешается в период проведения регистрационных действий, порядок которых определен Советом Министров Республики Беларусь.', 'Запрещается.', NULL, NULL, '2', 'Изучите п.194.2 ПДД', '/VIEW/RESOURCES/IMG/6-1-10.jpg'),
  --7
  (0, 'В чем из перечисленного заключается опасность длительного торможения автомобиля с выключенной передачей на крутых затяжных спусках?', 'В повышенном износе деталей сцепления.', 'В повышенном износе шин.', 'В повышенном износе деталей двигателя.', 'В перегреве тормозов и отказ их в работе.', '4', NULL, '/VIEW/RESOURCES/IMG/7-1-1.jpg'),
  (0, 'Для чего применяется 5%-ный спиртовой раствор йода, входящий в комплект медицинской аптечки, которой оснащены механические транспортные средства?', 'Как средство, успокаивающее боли в области сердца.', 'Для вдыхания при обмороке, угаре.', 'Для обработки края раны при повреждении кожи и тканей.', NULL, '3', NULL, '/VIEW/RESOURCES/IMG/7-1-2.jpg'),
  (0, 'В каком случае из перечисленных транспортировка пострадавших осуществляется сидя?', 'При черепно-мозговой травме.', 'При наличии повреждений верхних конечностей, органов брюшной полости.', 'При переломе ключицы, переломе ребер.', NULL, '3', NULL, NULL),
  (0, 'Как правильно транспортировать пострадавшего с переломом ключицы?', 'Лежа на животе.', 'Лежа на спине.', 'Сидя.', 'Лежа на боку.', '3', NULL, NULL),
  (0, 'Что следует сделать водителю, чтобы предотвратить возникновение заноса при поезде крутого поворота при управлении автомобилем с механической коробкой передач?', 'Перед поворотом снизить скорость, при необходимости включить пониженную передачу, а при проезде поворота не увеличивать резко скорость и не тормозить.', 'Перед поворотом снизить скорость и выжать педаль сцепления, чтобы дать возможность автомобилю двигатья накатом на повороте.', 'Допускается любое из перечисленных действий.', NULL, '1', NULL, '/VIEW/RESOURCES/IMG/7-1-5.jpg'),
  (0, 'Как остановить кровотечение при ранении артерии с использованием вложений, входящих в состав автомобильной аптечки?', 'Наложить жгут выше места ранения и под жгутом обязательно оставить записку со временем его наложения. В любое время года ослаблять жгут на 10-15 минут не реже чем раз в 30-40 минут от момента его наложения.', 'Наложить жгут ниже места ранения.', NULL, NULL, '1', NULL, '/VIEW/RESOURCES/IMG/7-1-6.jpg'),
  (0, 'Для экстренной остановки автомобиля с механической коробкой передач на сухой дороге необходимо:', 'Выключить передачу и резко нажать на педаль тормоза.', 'Выключить сцепление, после чего незамедлительно нажать на педаль тормоза.', 'Нажать на педаль тормоза, не выключая сцепления.', NULL, '3', NULL, NULL),
  (0, 'Водитель может быть признан виновным в совершении дорожно-транспортного происшествия и подвергнут уголовному наказанию по решению:', 'Государственной автомобильной инспекции.', 'Органов предварительного расследования.', 'Суда.', 'Органов прокуратуры.', '3', NULL, NULL),
  (0, 'Как изменяется устойчивость автомобиля на подъеме и косогоре с повышением высоты центра тяжести?', 'Не меняется.', 'Увеличивается.', 'Уменьшается.', NULL, '3', NULL, NULL),
  (0, 'Что следует сделать в первую очередь для оказания помощи лежащему на земле пострадавшему без видимых наружных повреждений, но находящемуся в бессознательном состоянии?', 'Поднести к носу ватку с нашатырным спиртом, приподнять голову, дать питье.', 'Запрокинуть голову на затылок, открыть рот, при необходимости очистить ротовую полость от инородных масс, контролировать наличие дыхания и пульса, срочно вызвать "Скорую медицинскую помощь".', 'Подложить под ноги валик, срочно вызвать "Скорую медицинскую помощь".', NULL, '2', NULL, NULL);